mapper.select:
SELECT r.ID, decode(r.TYPE,'menu',m.HREF,r.NAME) NAME, r.TYPE, decode(r.TYPE,'menu',m.NAME,r.REMARK) REMARK, r.STATUS FROM SYS_RIGHT r LEFT JOIN SYS_MENU m on r.NAME = m.ID WHERE r.STATUS = ${@org.xij.web.module.base.BusinessEntity@STATUS_NORMAL}
AND r.TYPE LIKE concat(concat('%',#{type}),'%')
AND r.NAME LIKE concat(concat('%',#{name}),'%') or m.href LIKE concat(concat('%',#{name}),'%')

mapper.update:
UPDATE SYS_RIGHT
SET STATUS = ${@org.xij.web.module.base.BusinessEntity@STATUS_DELETED},
UPDATE_BY   = #{updateBy},
UPDATE_DEPT = #{updateDept},
UPDATE_TIME = #{updateTime}
WHERE ID = #{id}
  and STATUS = ${@org.xij.web.module.base.BusinessEntity@STATUS_NORMAL}

mapper.select:
SELECT ID, NAME, TYPE, REMARK, STATUS
FROM SYS_RIGHT
WHERE ID = #{id}
